import { Selection, BaseType, ArrayLike, ValueFn } from "d3-selection";

declare module "d3-selection" {
  interface Selection<
    GElement extends BaseType,
    Datum,
    PElement extends BaseType,
    PDatum
  > {
    /**
     *Alinea un grupo respecto a un grupo padre
     */
    alignment(): this;

    /**
     * Alinea el grupo, respecto al valor recibido
     */
    alignment(alignment: string): this;

    /**
     *Obtiene el valor de la propiedad ali
     */
    alignmentValue(): string;

    /**
     * Alinea un grupo con atrubutos especificos
     */
    alignmentAttr(attribute: string, value: string): this;

    /**
     * Crea un nuevo calendario
     */
    calendar(): this;

    /**
     * Obtiene el valor de un calendario
     * Retorna un Date
     */
    calendarValue(): Date;

    /**
     * Aplica tipo de fuente
     */
    applyFont(fuente: any): this;

    /**
     * Aplica color
     */
    applyColor(color: string): this;

    /**
     * crea un boton
     */
    boton(): this;

    cajaTexto(): this;

    textfieldvalue(): this;

    textarea(): this;
  }
}
